import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth/auth.service';
import { User } from '../models/user';
import { UserRole } from '../models/user';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent implements OnInit {
  currentUser: User | null = null;
  UserRole = UserRole;

  constructor(public authService: AuthService) {}

  ngOnInit(): void {
    this.currentUser = this.authService.currentUserValue;
  }
}
<div *ngIf="currentUser">
  <h1>Welcome, {{currentUser.username}}!</h1>

  <div *ngIf="currentUser.role === UserRole.MANAGER">
    <p>You have access to all functionalities:</p>
    <ul>
      <li><a routerLink="/menu">Menu</a></li>
      <li><a routerLink="/order">Order</a></li>
      <li><a routerLink="/user">User</a></li>
    </ul>
  </div>

  <div *ngIf="currentUser.role === UserRole.CHEF">
    <p>You have access to:</p>
    <ul>
      <li><a routerLink="/menu">Menu</a></li>
      <li><a routerLink="/order">Order</a></li>
    </ul>
  </div>

  <div *ngIf="currentUser.role === UserRole.WAITER">
    <p>You have access to:</p>
    <ul>
      <li><a routerLink="/menu">Menu</a></li>
      <li><a routerLink="/order">Order</a></li>
    </ul>
  </div>

  <div *ngIf="currentUser.role === UserRole.CLIENT">
    <p>You have access to:</p>
    <ul>
      <li><a routerLink="/menu">Menu</a></li>
      <li><a routerLink="/order">Order</a></li>
    </ul>
  </div>

  <button (click)="authService.logout()">Logout</button>
</div>
